import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent implements OnInit {
  isDarkMode = false;
  isMobileMenuOpen = false;

  constructor() { }

  ngOnInit(): void {
    // Check if user has a saved preference
    const savedTheme = localStorage.getItem('theme');
    if (savedTheme) {
      this.isDarkMode = savedTheme === 'dark';
      this.applyTheme();
    } else {
      // Check if user prefers dark mode in their system
      if (window.matchMedia && window.matchMedia('(prefers-color-scheme: dark)').matches) {
        this.isDarkMode = true;
        this.applyTheme();
      }
    }
  }

  toggleTheme(): void {
    this.isDarkMode = !this.isDarkMode;
    this.applyTheme();
    // Save user preference
    localStorage.setItem('theme', this.isDarkMode ? 'dark' : 'light');
  }

  toggleMobileMenu(): void {
    this.isMobileMenuOpen = !this.isMobileMenuOpen;
  }

  private applyTheme(): void {
    document.body.classList.toggle('dark-theme', this.isDarkMode);
  }
}
<header class="main-header">
  <nav class="navbar">
    <div class="logo">
      <!-- <img src="assets/images/icons/portfolio.png" alt="Portfolio Icon" class="portfolio-icon"> -->
       <img src="assets/images/icons/cropped_circle_image.png" height="34px" width="34px" alt="">
    </div>

    <!-- Mobile menu toggle button -->
    <button class="mobile-menu-toggle" (click)="toggleMobileMenu()">
      <i class="bi bi-list"></i>
    </button>

    <div class="nav-links" [class.active]="isMobileMenuOpen">
      <a routerLink="/home" routerLinkActive="active">Home</a>
      <a routerLink="/about" routerLinkActive="active">About</a>
      <!-- <a routerLink="/contact" routerLinkActive="active">Contact</a> -->
