import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-contact',
  templateUrl: './contact.component.html',
  styleUrls: ['./contact.component.scss']
})
export class ContactComponent implements OnInit {
  isDarkMode = false;
  contactForm: FormGroup;

  constructor(private fb: FormBuilder) {
    this.contactForm = this.fb.group({
      name: ['', [Validators.required]],
      email: ['', [Validators.required, Validators.email]],
      message: ['', [Validators.required]]
    });
  }

  ngOnInit(): void {
    // Check if dark mode was previously set in localStorage
    const savedMode = localStorage.getItem('darkMode');
    if (savedMode) {
      this.isDarkMode = savedMode === 'true';
      this.applyTheme();
    }
  }

  toggleDarkMode(): void {
    this.isDarkMode = !this.isDarkMode;
    localStorage.setItem('darkMode', this.isDarkMode.toString());
    this.applyTheme();
  }

  onSubmit(): void {
    if (this.contactForm.valid) {
      // Here you would typically send the form data to your backend
      console.log('Form submitted with data:', this.contactForm.value);

      // Reset form after submission
      this.contactForm.reset();

      // You could display a success message here
      alert('Thank you for your message! I will get back to you soon.');
    } else {
      // Mark all fields as touched to trigger validation messages
      Object.keys(this.contactForm.controls).forEach(key => {
        this.contactForm.get(key)?.markAsTouched();
      });
    }
  }

  private applyTheme(): void {
    const contactContainer = document.querySelector('.contact-container');
    if (contactContainer) {
      if (this.isDarkMode) {
        contactContainer.classList.add('dark-mode');
        contactContainer.classList.remove('light-mode');
      } else {
        contactContainer.classList.add('light-mode');
        contactContainer.classList.remove('dark-mode');
      }
    }
  }
}
<div class="contact-container" [ngClass]="isDarkMode ? 'dark-mode' : 'light-mode'">
  <div class="contact-content">
    <div class="left-section">
      <h2>Contact Me</h2>
      <form class="contact-form" (ngSubmit)="onSubmit()" [formGroup]="contactForm">
        <div class="form-group">
          <label for="name">Name</label>
          <input type="text" id="name" placeholder="Your Name" formControlName="name" required>
          <div *ngIf="contactForm.get('name')?.invalid && contactForm.get('name')?.touched" class="error-message">
            Please enter your name
          </div>
        </div>
        <div class="form-group">
          <label for="email">Email</label>
          <input type="email" id="email" placeholder="Your Email" formControlName="email" required>
          <div *ngIf="contactForm.get('email')?.invalid && contactForm.get('email')?.touched" class="error-message">
            Please enter a valid email
          </div>
        </div>
        <div class="form-group">
          <label for="message">Message</label>
          <textarea id="message" placeholder="Your Message" rows="5" formControlName="message" required></textarea>
          <div *ngIf="contactForm.get('message')?.invalid && contactForm.get('message')?.touched" class="error-message">
            Please enter your message
          </div>
        </div>
        <button type="submit" class="submit-btn" [disabled]="contactForm.invalid">Send Message</button>
      </form>
    </div>

    <div class="right-section">
      <div class="theme-toggle-container">
        <div class="theme-toggle">
          <span class="light-icon">‚òÄÔ∏è</span>
          <label class="switch">
            <input type="checkbox" [checked]="isDarkMode" (change)="toggleDarkMode()">
            <span class="slider round"></span>
          </label>
          <span class="dark-icon">üåô</span>
        </div>
        <span class="mode-text">{{ isDarkMode ? 'Dark Mode' : 'Light Mode' }}</span>
      </div>

      <div class="contact-info">
        <h3>Contact Information</h3>
        <ul>
          <li><i class="fa fa-envelope"></i> phanipspkmech@gmail.com</li>
          <li><i class="fa fa-phone"></i> 6302366346</li>
          <li><i class="fa fa-map-marker"></i> Old Guntur, Guntur District, Andhra Pradesh</li>
        </ul>
      </div>

      <div class="social-links">
        <h3>Follow Me</h3>
        <div class="social-icons">
          <a href="https://www.linkedin.com/in/phani-kumar-b10857268" class="social-icon" target="_blank" rel="noopener noreferrer"><i class="fa fa-linkedin"></i></a>
          <a href="https://github.com/YourGitHubUsername" class="social-icon" target="_blank" rel="noopener noreferrer"><i class="fa fa-github"></i></a>
          <!-- Update with your actual Twitter/X username -->
          <a href="https://twitter.com/YourTwitterUsername" class="social-icon" target="_blank" rel="noopener noreferrer"><i class="fa fa-twitter"></i></a>
        </div>
      </div>
    </div>
  </div>
</div>
